<?php include './includes/header.php' ?>
<?php include './config/db.php' ?>
<?php
   $id = isset($_GET['id']) ? mysqli_real_escape_string($conn, $_GET['id']) : null;
   //query to fetch data
   $qry = "SELECT * FROM eventTbl WHERE eventId='$id';";
   //result
   $result = mysqli_query($conn,$qry);
   //get the array for the event
   $event = mysqli_fetch_assoc($result);

   if(!$event){
       echo 'No event found';
   }

   $speakerId = uniqid();

   $reg_qry = "SELECT * FROM registrationTbl WHERE eventId='$id';";
   $reg_qry_res = mysqli_query($conn,$reg_qry);
   $registrations = mysqli_fetch_all($reg_qry_res,MYSQLI_ASSOC);
  

   if(!$registrations){
      echo 'No registrations yet.';
   }
?>

